let patientName = 'Unknown';
if (patient && patient.name && patient.name[0]) {
  patientName = `${patient.name[0].given?.join(' ') || ''} ${patient.name[0].family || ''}`.trim();
}

console.log(`Found ${diabeteConditions.length} active diabetes condition(s) for patient ${patientName}.`);

// Find PDF attachments
const pdfAttachments = fullEhr.attachments.filter(a => a.contentType === 'application/pdf');
console.warn(`Found ${pdfAttachments.length} PDF attachments.`);

